// MAC_CSTRING.H// Copyright (c) 2000 Joe Riedel, Nick Randal.// Author: Joe Riedel.#ifndef MAC_CSTRING_H#define MAC_CSTRING_H#include <string.h>// Replacement class to MFC  CString.class CString{private:	char* m_pString;	int m_nLength;		void FreeData();	public:	CString();	CString(const char* pString);	CString(const CString& ToCopy);	virtual ~CString();		void Replace(char a, char b);	bool IsEmpty();		int Find(const char* szString);	int Delete(int nIndex, int nCount);		const CString& operator = (const CString& sSrc);	const char* operator = (const char* szSrc);	CString& operator += (const CString& sOther);	CString& operator += (const char* szOther);	CString& operator += (const char c);	char operator[](int nIndex) const;	friend bool operator != (const CString& sString, const CString& sOther);	friend bool operator != (const CString& sString, const char* szOther);	friend bool operator == (const CString& sString, const CString& sOther);	friend bool operator == (const CString& sString, const char* szOther);	friend CString operator + (const CString& sString, const CString& sOther);	friend CString operator + (const CString& sString, const char* szOther);	friend CString operator + (const char* szString, const CString& sString);	void ConvertUnixPath();	operator const char*() const;		void Format(const char* format, ...);		void MakeLower();	void MakeUpper();	int GetLength();	};#endif