// MAC_REGISTRY.H// Copyright (c) 2000 Joe Riedel, Nick Randal.// Author: Joe Riedel.#ifndef MAC_REGISTRY_H#define MAC_REGISTRY_H#include "tread3d.h"#include "LinkedList.h"struct REGENTRY{	struct REGENTRY* prev, *next;		int nType;	CString sPath;	CString sName;		int nVal;	CString sVal;};// The win32 version of Tread stores stuff in the registry, so// on the mac we make a fake data file that stores the equivelent stuff.class CMacRegistry{private:	CLinkedList<REGENTRY> m_RegList;		bool ReadRegFile(const CString& sPath);	bool WriteRegFile(const CString& sPath);	REGENTRY* FindEntry(const CString& sPath, const CString& sName, const int nType);	public:	CString m_pString[STRING_MAX];	int m_pInt[MAX_INT];	bool m_pBool[BOOL_MAX];		CMacRegistry();	virtual ~CMacRegistry();		bool InitRegistry();	bool FlushRegistry();		int GetProfileInt(const char* szPath, const char* szName, int nDefault=0);	void WriteProfileInt(const char* szPath, const char* szName, int nValue);	CString GetProfileString(const char* szPath, const char* szName, const char* szDefault=NULL);	void WriteProfileString(const char* szPath, const char* szName, const char* szValue);};CMacRegistry* GetTreadMacRegistry();#endif